import axios from "axios";
import { MeetingRoomsData, iRoomCards } from "../interfaces/interfaces";

const getRooms = async (): Promise<MeetingRoomsData[]> => {
    const result = await axios.get("10.152.20.113:3001/rooms");
    return result.data;
};

const getAllRooms = async (): Promise<iRoomCards[]> => {
    return (await axios.get(`10.152.20.113:3001/rooms/`)).data;
};

const getRoomById = async (id: number) => {
    return await axios.get(`10.152.20.113:3001/rooms/${id}`);
};

const deleteRooms = async (id: number) => {
    return await axios.delete(`10.152.20.113:3001/rooms/${id}`);
};

const addRoom = async (room: MeetingRoomsData) => {
    return await axios.post("10.152.20.113:3001/rooms", room);
};


const updateRoomData = async (room: MeetingRoomsData) => {
    return await axios.put(`10.152.20.113:3001/rooms/${room.id}`, room);
};

export {
    deleteRooms,
    addRoom,
    updateRoomData,
    getRooms,
    getRoomById,
    getAllRooms,
};
